import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { Meeting } from '../../models/meeting.model';
import { MeetingService } from '../../services/meeting.service';

@Component({
  selector: 'app-admin-meetings',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './admin-meetings.component.html',
  styleUrls: ['./admin-meetings.component.css']
})
export class AdminMeetingsComponent implements OnInit {
  meetings: Meeting[] = [];
  loading = false;
  error: string | null = null;

  constructor(private meetingService: MeetingService) {}

  ngOnInit(): void {
    this.loadMeetings();
  }

  loadMeetings(): void {
    this.loading = true;
    this.error = null;
    this.meetingService.getAllMeetings().subscribe({
      next: (data) => {
        this.meetings = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load meetings';
        this.loading = false;
        console.error('Error loading meetings:', err);
      }
    });
  }

  deleteMeeting(id: number | undefined): void {
    if (!id) return;
    
    if (confirm('Are you sure you want to delete this meeting?')) {
      this.meetingService.deleteMeeting(id).subscribe({
        next: () => {
          this.loadMeetings();
        },
        error: (err) => {
          this.error = 'Failed to delete meeting';
          console.error('Error deleting meeting:', err);
        }
      });
    }
  }

  getParticipantNames(meeting: Meeting): string {
    return meeting.participants?.map(p => p.name).join(', ') || 'None';
  }

  formatDateTime(dateTime: string): string {
    return new Date(dateTime).toLocaleString();
  }
}
<div class="admin-meetings-container">
  <div class="header">
    <h2>All Meetings</h2>
    <button routerLink="/admin/meetings/new" class="btn-primary">Create Meeting</button>
  </div>
  
  <div *ngIf="loading" class="loading">Loading meetings...</div>
  <div *ngIf="error" class="error">{{ error }}</div>
  
  <div *ngIf="!loading && !error" class="meetings-table">
    <table>
      <thead>
        <tr>
          <th>Title</th>
          <th>Organizer</th>
          <th>Start Time</th>
          <th>End Time</th>
          <th>Participants</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let meeting of meetings">
          <td>{{ meeting.title }}</td>
          <td>{{ meeting.organizer.name }}</td>
          <td>{{ formatDateTime(meeting.startTime) }}</td>
          <td>{{ formatDateTime(meeting.endTime) }}</td>
          <td>{{ getParticipantNames(meeting) }}</td>
          <td class="actions">
            <button [routerLink]="['/admin/meetings/edit', meeting.id]" class="btn-edit">Edit</button>
            <button (click)="deleteMeeting(meeting.id)" class="btn-delete">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
  
  <div *ngIf="!loading && meetings.length === 0" class="no-data">
    No meetings found.
  </div>
</div>
