import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Agenda } from '../../models/agenda.model';
import { User } from '../../models/user.model';
import { AgendaService } from '../../services/agenda.service';
import { UserService } from '../../services/user.service';

@Component({
  selector: 'app-admin-agendas',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './admin-agendas.component.html',
  styleUrls: ['./admin-agendas.component.css']
})
export class AdminAgendasComponent implements OnInit {
  agendas: Agenda[] = [];
  users: User[] = [];
  selectedUserId: number | null = null;
  loading = false;
  error: string | null = null;

  constructor(
    private agendaService: AgendaService,
    private userService: UserService
  ) {}

  ngOnInit(): void {
    this.loadUsers();
  }

  loadUsers(): void {
    this.userService.getAllUsers().subscribe({
      next: (data) => {
        this.users = data;
      },
      error: (err) => {
        this.error = 'Failed to load users';
        console.error('Error loading users:', err);
      }
    });
  }

  loadAgendasForUser(): void {
    const userId = this.selectedUserId !== null ? Number(this.selectedUserId) : 0;
    if (!userId) {
      this.agendas = [];
      return;
    }

    this.loading = true;
    this.error = null;
    this.agendaService.getAgendasByUser(userId).subscribe({
      next: (data) => {
        this.agendas = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = err.error?.message || 'Failed to load agendas';
        this.loading = false;
        console.error('Error loading agendas:', err);
      }
    });
  }

  deleteAgenda(id: number | undefined): void {
    if (!id) return;
    
    if (confirm('Are you sure you want to delete this agenda?')) {
      this.agendaService.deleteAgenda(id).subscribe({
        next: () => {
          this.loadAgendasForUser();
        },
        error: (err) => {
          this.error = 'Failed to delete agenda';
          console.error('Error deleting agenda:', err);
        }
      });
    }
  }

  formatDate(date: string): string {
    return new Date(date).toLocaleDateString();
  }
}
<div class="admin-agendas-container">
  <h2>All User Agendas</h2>
  
  <div class="user-selector">
    <label for="userSelect">Select User:</label>
    <select 
      id="userSelect" 
      [(ngModel)]="selectedUserId" 
      (change)="loadAgendasForUser()"
      class="form-control"
    >
      <option [value]="null">-- Select a user --</option>
      <option *ngFor="let user of users" [value]="user.id">
        {{ user.name }} ({{ user.email }})
      </option>
    </select>
  </div>
  
  <div *ngIf="loading" class="loading">Loading agendas...</div>
  <div *ngIf="error" class="error">{{ error }}</div>
  
  <div *ngIf="!loading && !error && selectedUserId" class="agendas-table">
    <table>
      <thead>
        <tr>
          <th>Date</th>
          <th>Start Time</th>
          <th>End Time</th>
          <th>Status</th>
          <th>Meeting</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let agenda of agendas">
          <td>{{ formatDate(agenda.date) }}</td>
          <td>{{ agenda.startTime }}</td>
          <td>{{ agenda.endTime }}</td>
          <td>
            <span [class]="'status-badge status-' + agenda.status.toLowerCase()">
              {{ agenda.status }}
            </span>
          </td>
          <td>{{ agenda.meetingTitle || 'N/A' }}</td>
          <td class="actions">
            <button (click)="deleteAgenda(agenda.id)" class="btn-delete">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
  
  <div *ngIf="!loading && selectedUserId && agendas.length === 0" class="no-data">
    No agendas found for this user.
  </div>
  
  <div *ngIf="!selectedUserId && !loading" class="no-data">
    Please select a user to view their agendas.
  </div>
</div>
