import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { Meeting } from '../../models/meeting.model';
import { MeetingService } from '../../services/meeting.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-meeting-list',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './meeting-list.component.html',
  styleUrls: ['./meeting-list.component.css']
})
export class MeetingListComponent implements OnInit {
  meetings: Meeting[] = [];
  loading = false;
  error: string | null = null;

  constructor(
    private meetingService: MeetingService,
    public authService: AuthService
  ) {}

  ngOnInit(): void {
    this.loadMeetings();
  }

  loadMeetings(): void {
    this.loading = true;
    this.error = null;
    this.meetingService.getAllMeetings().subscribe({
      next: (data) => {
        this.meetings = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load meetings';
        this.loading = false;
        console.error('Error loading meetings:', err);
      }
    });
  }

  deleteMeeting(id: number | undefined): void {
    if (!id) return;
    
    if (confirm('Are you sure you want to delete this meeting?')) {
      this.meetingService.deleteMeeting(id).subscribe({
        next: () => {
          this.loadMeetings();
        },
        error: (err) => {
          this.error = 'Failed to delete meeting';
          console.error('Error deleting meeting:', err);
        }
      });
    }
  }

  formatDateTime(dateTime: string): string {
    return new Date(dateTime).toLocaleString();
  }
}
<div class="meeting-list-container">
  <h2>Meetings</h2>
  
  <div *ngIf="loading" class="loading">Loading meetings...</div>
  <div *ngIf="error" class="error">{{ error }}</div>
  
  <div *ngIf="!loading && !error" class="meetings-grid">
    <div *ngFor="let meeting of meetings" class="meeting-card">
      <h3>{{ meeting.title }}</h3>
      <p *ngIf="meeting.description" class="description">{{ meeting.description }}</p>
      <div class="meeting-details">
        <p><strong>Start:</strong> {{ formatDateTime(meeting.startTime) }}</p>
        <p><strong>End:</strong> {{ formatDateTime(meeting.endTime) }}</p>
        <p><strong>Organizer:</strong> {{ meeting.organizer.name }}</p>
        <p><strong>Participants:</strong> 
          <span *ngIf="meeting.participants.length > 0">
            {{ meeting.participants.length }} participant(s)
          </span>
          <span *ngIf="meeting.participants.length === 0">None</span>
        </p>
      </div>
      <div class="actions">
        <button (click)="deleteMeeting(meeting.id)" class="btn-delete">Delete</button>
      </div>
    </div>
  </div>
  
  <div *ngIf="!loading && meetings.length === 0" class="no-data">
    No meetings found.
  </div>
</div>
