import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet, RouterLink, RouterLinkActive, Router } from '@angular/router';
import { AuthService } from './services/auth.service';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, RouterLink, RouterLinkActive],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Meeting Planner';

  constructor(
    public authService: AuthService,
    private router: Router
  ) {}

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<div class="app-container">
  <nav class="navbar" *ngIf="authService.isLoggedIn()">
    <div class="nav-brand">
      <h1>{{ title }}</h1>
    </div>
    <ul class="nav-menu">
      <li>
        <a routerLink="/users" routerLinkActive="active">Users</a>
      </li>
      <li>
        <a routerLink="/users/new" routerLinkActive="active">New User</a>
      </li>
      <li>
        <a routerLink="/meetings" routerLinkActive="active">Meetings</a>
      </li>
      <li>
        <a routerLink="/meetings/new" routerLinkActive="active">New Meeting</a>
      </li>
      <li>
        <a routerLink="/agendas" routerLinkActive="active">Agendas</a>
      </li>
    </ul>
    <div class="nav-user">
      <span class="user-name">{{ (authService.currentUser$ | async)?.name }}</span>
      <button (click)="logout()" class="btn-logout">Logout</button>
    </div>
  </nav>
  
  <main class="main-content">
    <router-outlet></router-outlet>
  </main>
</div>
