import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Agenda } from '../../models/agenda.model';
import { User } from '../../models/user.model';
import { AgendaService } from '../../services/agenda.service';
import { UserService } from '../../services/user.service';

@Component({
  selector: 'app-agenda-list',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './agenda-list.component.html',
  styleUrls: ['./agenda-list.component.css']
})
export class AgendaListComponent implements OnInit {
  agendas: Agenda[] = [];
  users: User[] = [];
  selectedUserId: number = 0;
  loading = false;
  error: string | null = null;

  constructor(
    private agendaService: AgendaService,
    private userService: UserService
  ) {}

  ngOnInit(): void {
    this.loadUsers();
  }

  loadUsers(): void {
    this.userService.getAllUsers().subscribe({
      next: (data) => {
        this.users = data;
        if (data.length > 0) {
          this.selectedUserId = data[0].id!;
          this.loadAgendas();
        }
      },
      error: (err) => {
        this.error = 'Failed to load users';
        console.error('Error loading users:', err);
      }
    });
  }

  loadAgendas(): void {
    if (!this.selectedUserId) return;
    
    this.loading = true;
    this.error = null;
    this.agendaService.getAgendasByUser(this.selectedUserId).subscribe({
      next: (data) => {
        this.agendas = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load agendas';
        this.loading = false;
        console.error('Error loading agendas:', err);
      }
    });
  }

  onUserChange(): void {
    this.loadAgendas();
  }

  deleteAgenda(id: number | undefined): void {
    if (!id) return;
    
    if (confirm('Are you sure you want to delete this agenda?')) {
      this.agendaService.deleteAgenda(id).subscribe({
        next: () => {
          this.loadAgendas();
        },
        error: (err) => {
          this.error = 'Failed to delete agenda';
          console.error('Error deleting agenda:', err);
        }
      });
    }
  }
}
<div class="agenda-list-container">
  <h2>Agendas</h2>
  
  <div class="user-selector">
    <label for="userSelect">Select User:</label>
    <select 
      id="userSelect" 
      [(ngModel)]="selectedUserId" 
      (change)="onUserChange()"
      class="form-control"
    >
      <option *ngFor="let user of users" [value]="user.id">{{ user.name }}</option>
    </select>
  </div>
  
  <div *ngIf="loading" class="loading">Loading agendas...</div>
  <div *ngIf="error" class="error">{{ error }}</div>
  
  <div *ngIf="!loading && !error" class="agendas-grid">
    <div *ngFor="let agenda of agendas" class="agenda-card">
      <h3>{{ agenda.userName }}</h3>
      <div class="agenda-details">
        <p><strong>Date:</strong> {{ agenda.date }}</p>
        <p><strong>Time:</strong> {{ agenda.startTime }} - {{ agenda.endTime }}</p>
        <p><strong>Status:</strong> 
          <span [class]="'status-' + agenda.status.toLowerCase()">
            {{ agenda.status }}
          </span>
        </p>
      </div>
      <div class="actions">
        <button (click)="deleteAgenda(agenda.id)" class="btn-delete">Delete</button>
      </div>
    </div>
  </div>
  
  <div *ngIf="!loading && agendas.length === 0" class="no-data">
    No agendas found for this user.
  </div>
</div>
